
public class b_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3;
		int b=1;
		System.out.println("A \t B \t pow(A,B)");
		System.out.println(a+" \t "+b+" \t "+(int)Math.pow(a, b));
		a+=2;b++;
		System.out.println(a+" \t "+b+" \t "+(int)Math.pow(a, b));
		a+=2;b++;
		System.out.println(a+" \t "+b+" \t "+(int)Math.pow(a, b));
		a+=2;b++;
		System.out.println(a+" \t "+b+" \t "+(int)Math.pow(a, b));
	}

}
